export declare const ansibleTemplate = "-\n  name: '{{ request.urlWithoutQuery }}'\n  uri:\n    url: '{{ request.url }}'\n    method: {{ request.method | upper }}\n{%- if (request.data | isString) and (request.data) %}\n    body:\n      {{ data | dump }}\n    body_format: json\n{%- endif %}\n{%- if request.headers %}\n    headers:\n    {%- for key, value in request.headers %}\n      {{ key }}: '{{ value }}'\n    {%- endfor %}\n{%- endif %}\n{%- if request.auth %}\n    {%- set url_username = request.auth[0] %}\n    {%- set url_password = request.auth[1] %}\n    {%- if url_username %}\n    url_username: {{ url_username }}\n    {%- endif %}\n    {%- if url_password %}\n    url_password: {{ url_password }}\n    {%- endif %}\n{%- endif %}\n{%- if request.insecure %}\n    validate_certs: no\n{%- endif %}\n  register: result\n";
